firestore.rules:
rules_version = '2';
service cloud.firestore {
  match /databases/{database}/documents {

    // User Profiles
    match /users/{userId} {
      allow read, write: if request.auth != null && request.auth.uid == userId;
    }

    // Trips
    match /trips/{tripId} {
      // Anyone can read a public trip, logged-in users can read trips they own or are shared on.
      allow get: if resource.data.visibility == 'public' || 
                   (request.auth != null && (
                     resource.data.ownerId == request.auth.uid || 
                     (resource.data.sharedWith != null && request.auth.uid in resource.data.sharedWith)
                   ));

      // Logged-in users can query the trips collection. 
      // The queries in the app will filter for public trips or trips owned/shared with the user.
      allow list: if request.auth != null;

      // Only the owner can create, update, or delete a trip.
      allow create: if request.auth != null && request.auth.uid == request.resource.data.ownerId;
      allow update, delete: if request.auth != null && request.auth.uid == resource.data.ownerId;

      // Entries subcollection
      match /entries/{entryId} {
        // Read access for authenticated users, further filtering is done in the app.
        allow get, list: if request.auth != null;

        // Only the trip owner or original entry author can write to an entry.
        allow create, update, delete: if request.auth != null && (
                                     request.auth.uid == get(/databases/$(database)/documents/trips/$(tripId)).data.ownerId ||
                                     request.auth.uid == resource.data.authorId
                                  );

        // Comments subcollection
        match /comments/{commentId} {
            // Allow read if the trip the comment belongs to is public or user has access.
            allow read: if get(/databases/$(database)/documents/trips/$(tripId)).data.visibility == 'public' ||
                          (request.auth != null && (
                            get(/databases/$(database)/documents/trips/$(tripId)).data.ownerId == request.auth.uid ||
                            (get(/databases/$(database)/documents/trips/$(tripId)).data.sharedWith != null && request.auth.uid in get(/databases/$(database)/documents/trips/$(tripId)).data.sharedWith)
                          ));

            // Only authenticated users can create comments.
            allow create: if request.auth != null && request.auth.uid == request.resource.data.authorId;

            // Only the author can update/delete their own comment.
            allow update, delete: if request.auth != null && request.auth.uid == resource.data.authorId;
        }
      }
    }

    // Routes
    match /routes/{routeId} {
      // Access is controlled based on the associated trip's permissions.
      allow read, write: if request.auth != null && get(/databases/$(database)/documents/trips/$(resource.data.tripId)).data.ownerId == request.auth.uid;
    }
  }
}
